import "./contact.css";
import axios from "axios";
import React, { useState, useRef } from "react";
import emailjs from "@emailjs/browser";

const ContactUs = () => {
  const form = useRef();
  const [error, setError] = useState(null);

  const [myData, setmyData] = useState({
    fullName: "",
    mail: "",
    Message: "",
  });
  const { fullName, mail, Message } = myData;

  const onChange = (e) => {
    setmyData({ ...myData, [e.target.name]: e.target.value });
  };

  const sendEmail = async (e) => {
    e.preventDefault();
    try {
      await emailjs.sendForm(
        "service_51q4gui",
        "template_b9ajebw",
        form.current,
        "xGN8ZyR34jEIrY5x2"
      );

      console.log("email sent successfully");
      form.current.reset();
    } catch (error) {
      console.log("email sending failed", error);
    }
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!mail) {
      setError("Email is required");
      return;
    }
    setError(null);
    const newContact = {
      fullName: fullName,
      mail: mail,
      Message: Message,
    };
    await sendEmail(e);

    try {
      await axios.post("http://localhost:5000/contactus", newContact);
      setmyData({
        fullName: "",
        mail: "",
        Message: "",
      });
    } catch (err) {
      console.log("error", err.response.data);
    }
  };

  return (
    <>
      <div className="divplus">
        <div className="contact-us">
          <h1>CONTACT US</h1>
          <form className="contact-form" ref={form} onSubmit={onSubmit}>
            <input
              type="text"
              name="fullName"
              value={fullName}
              placeholder="Enter your Full name"
              onChange={onChange}
              required
            />

            <input
              type="text"
              name="mail"
              value={mail}
              placeholder="Enter your email "
              onChange={onChange}
              required
            />
            {error === "Email is required" && (
              <div style={{ color: "red" }}>{error}</div>
            )}
            <input
              className="message"
              type="text"
              name="Message"
              value={Message}
              placeholder="Enter your Message"
              onChange={onChange}
              required
            />
            <button className="form-sbmt" type="submit" onClick={onSubmit}>
              SEND
            </button>
          </form>
        </div>
      </div>
    </>
  );
};

export default ContactUs;
